func findTheDifference(s string, t string) string {
	//concat string
	statement := s + t
	length := len(s + t)
	//create map of unique values
	flags := map[string]bool{}

	i := 0
	for j := range statement {
		if !flags[string(statement[j])] {
			flags[string(statement[j])] = true
			statement = statement + string(statement[j])
			i++

		}
	}
	//slice the string to save the not repeated value
	//statement = statement[length+len(s):]
	if i > 1 {
		statement = statement[length:]

	}
	// else {
	// 	statement = statement[length:]

	// }
	fmt.Println(len(statement))

	//return byte(statement[0])
	return statement

}
